import 'bulma/css/bulma.min.css'
import React, { Component } from "react"


class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      viewCompleted: false,
      activeItem: {
        title: "",
        description: "",
        completed: false
      },
      inventoryList: []
      };
  }

    // async componentDidMount() {
    //   try {
    //     const res = await fetch('http://localhost:8000/api/inventories/');
    //     const inventoryList = await res.json();
    //     this.setState({
    //       inventoryList
    //     });
    //   } catch (e) {
    //     console.log(e);
    // }
    // }
    render() {
      const { viewCompleted } = this.state;
      console.log(this.state.inventoryList.results)
      const newItems = this.state.inventoryList.results.filter(
        item => item.completed === viewCompleted
      );
      return(newItems.map(item => (
        <li 
          key={item.id}
          className="list-group-item d-flex justify-content-between align-items-center"
        >
          <span 
            className={`inventory-title mr-2 ${
              this.state.viewCompleted ? "completed-inventory" : ""
            }`}
            title={item.description}
            >
              {item.title}
            </span>
        </li>
      )));
    };

}

export default App;
