import React, { Component } from "react"
import 'bulma/css/bulma.min.css'
import { Button } from 'react-bulma-components'
import { Columns } from 'react-bulma-components'
import { Notification } from 'react-bulma-components'
import { render } from "@testing-library/react"
import { Container } from 'react-bulma-components'
import { Content } from 'react-bulma-components'

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      viewCompleted: false,
      activeItem: {
        title: "",
        description: "",
        completed: false
      },
      inventoryList: []
      };
  }

    async componentDidMount() {
      try {
        const res = await fetch('http://localhost:8000/api/inventorys/');
        const inventoryList = await res.json();
        this.setState({
          inventoryList
        });
      } catch (e) {
        console.log(e);
    }
    }
    render() {
      const { viewCompleted } = this.state;
      const newItems = this.state.inventoryList.filter(
        item => item.completed === viewCompleted
      );
      return newItems.map(item => (
        <li 
          key={item.id}
          className="list-group-item d-flex justify-content-between align-items-center"
        >
          <span 
            className={`inventory-title mr-2 ${
              this.state.viewCompleted ? "completed-inventory" : ""
            }`}
            title={item.description}
            >
              {item.title}
            </span>
        </li>
      ));
    };

}

export default App;
